mapper.insert:
insert
	into tbl_board
	(
		board_no
	,	board_type_no
	, board_dept
	,	board_title
	,	board_content
	,	emp_no
	, create_Date
	, use_YN  
	, board_check
	)

	values
	(
		board_no_seq.nextval
	, #{ boardTypeNo }
	, #{ boardDept }
	, #{ boardTitle }
	, #{ boardContent }
	, #{ empNo }
	, SYSDATE
	, 'Y'
	, #{ boardCheck }
	)

insert into tbl_boardfile ( BOARDFILE_NO , FILE_PATH , FILESYSTEM_NAME , ORIGINAL_NAME , UPLOAD_DATE , REF_TYPE , REF_NO , CREATER , CREATE_DATE , MODIFIER , MODIFY_DATE , USE_YN
) values ( boardfile_no_seq.nextval , #{filePath} , #{fileSystemName} , #{originalName} , SYSDATE , #{refType}
, board_no_seq.currval
, #{refNo}
, NULL , SYSDATE , NULL , NULL , 'Y'
)

mapper.update:
update
			 tbl_board
		set board_title   = #{boardTitle}
  		, board_content = #{boardContent}
 where board_no = #{boardNo}
