mapper.update:
UPDATE 
		TBL_EMPLOYEE
	SET 
		EMP_PHONE = #{empPhone}
	  , EMP_EMAIL = #{empEmail}
	  , EMP_POSTCODE = #{empPostcode}
	  , EMP_ADDRESS = #{empAddress}
	  , EMP_DETAILADDRESS = #{empDetailAddress}
 where EMP_NO = #{empNo}

UPDATE
		TBL_EMPLOYEE
	SET EMP_PASSWORD = #{empPwd}
		EMP_USE_YN = 'Y'
 WHERE EMP_NO = #{empNo}
