	   TBL_BOARDTYPE
   set BOARDT_NAME = #{boardtName}
	 , BOARDT_TYPE = #{boardtType}
	 , BOARDT_READ = #{boardtRead}
	 , BOARDT_WRITE = #{boardtWrite}
	 , BOARDT_CONTENT = #{boardtContent}
	 , BOARDT_USE = #{officeUse}
	 , OFFICE_USE = #{deptCode}
 where BOARD_TYPE_NO = #{boardTypeNo}
